$(document).ready(function(){
	/**
	 * 页面弹出div dialog
	 * @return {[type]} [description]
	 */
	$(".modal").click(function(){
        var a=$(this).data('modal');
        var w=$("#"+a).width();
        var h=$("#"+a).height();
        var ml='-'+Math.round(w/2);
        var mt='-'+Math.round(h/2);
        $("#"+a).css({
            "margin-left":+ml,
            "width":+w,
            "height":+h,
            "margin-top":+mt
        });
        $("#"+a).animate({
            "top":"50%",
            opacity: 'show'
        },200);
        $("#"+a).fadeIn(200);
    });
    $(".cls").click(function(){
        var id=$(this).data('id');
        $("#"+id).fadeOut(100);
         $("#"+id).animate({
            "top":"100%",
            opacity: 'hide'
        },200);
    });
    $(".dropdown").hover(function(){
        var id=$(this).data('id');
        $("#"+id).slideDown(200);
    })
    $("#user_menu").mouseleave(function(){
            $(this).slideUp(200);
    })
    /**
     * 滚动导航栏
     * @return {[type]} [description]
     */
    // $(window).scroll(function(){
    //     var a = 300;
    //     if ($(window).scrollTop()>a) {
    //         $(".header").addClass('fixed');
    //         $('.backTop').fadeIn(300);
    //     }else{
    //         $(".header").removeClass('fixed');
    //         $('.backTop').fadeOut(200);
    //     }
    // });
    $('.backTop').click(function(){
        $("body").scrollTop(0);
    });
}
)